const buttonCounter=document.querySelector("#btn-counter"),buttonClose=document.querySelector("#btn-close"),buttonReset=document.querySelector("#btn-reset"),popUp=document.querySelector(".layout"),counterText=document.querySelector(".counter"),state={getCounter:()=>+sessionStorage.getItem("CLICKS"),updateCounter:e=>sessionStorage.setItem("CLICKS",e),isReset:()=>{5<=state.getCounter()?buttonReset.style.display="block":buttonReset.style.display="none"},isTimes:()=>{counterText.innerText=1===state.getCounter()?state.getCounter()+" time":state.getCounter()+" times"}};buttonCounter.addEventListener("click",()=>{popUp.style.display="flex",state.updateCounter(state.getCounter()+1),state.isTimes(),state.isReset()}),popUp.addEventListener("click",e=>{e.currentTarget===e.target&&(popUp.style.display="none")}),buttonClose.addEventListener("click",()=>popUp.style.display="none"),buttonReset.addEventListener("click",()=>{state.updateCounter(0),state.isReset(),state.isTimes()});